+/// Using function write a program that can find the maximum number of 3 parameters.
+/// Explain.
+library;
+
+import "dart:io";
+
+int? readInt() {
+  while (true) {
+    String? line = stdin.readLineSync();
+    if (line == null) {
+      return null;
+    }
+    
+    try {
+      return int.tryParse(line);
+    } on (e) {
+      print("Invalid input! Try again.");
+    }
+  }
+}
+
+int max3(int a, int b, int c) {
+  int max = a;
+  if (b > max) {
+    max = b;
+  }
+  if (c > max) {
+    max = c;
+  }
+  return max;
+}
+
+void main() {
+  int a = stdin.readLineSync()!;
+  int b = 20;
+  int c = 30;
+  int max = a;
+  if (b > max) {
+    max = b;
+  }
+  if (c > max) {
+    max = c;
+  }
+  print("Max: $max");
+}
